import React from "react";

function App() {
const [count,setCount] = React.useState(0);
//destructuring
//const [red,green,blue] = [3,45,68];
console.log(count);
function increase(){
  setCount(count+1);
}
function decrease(){
  setCount(count-1);
}
  return (
    <div className="container">
      <h1>{count}</h1>
      <button onClick={increase}>+</button>
      <button onClick={decrease}>-</button>
    </div>
  );
}
export default App;
